
// score object stores the score from the local storage or if there is no score in local storage then it is assigned to default score object of wins: 0,losses: 0,ties: 0
let score = JSON.parse(localStorage.getItem('score')) || {
  //property : value
    wins: 0,
    losses: 0,
    ties: 0
  };

  updateScoreElement();//calls the updateScoreElement func to update the score as the page loads.

 
//playGame function runs on the button click
  function playGame(playerMove) {
    let result = '';
    // here computerMove variable is storing the computer move generated by the pickComputerMove() function;
    const computerMove = pickComputerMove();

    
    //Nested if-else-if statements to compare the player Move to the Computer Move
    if (playerMove === 'scissors') {
      if (computerMove === 'rock') {
        result = 'You lose.';
      } else if (computerMove === 'paper') {
        result = 'You win.';
      } else if (computerMove === 'scissors') {
        result = 'Tie.';
      }

    } else if (playerMove === 'paper') {
      if (computerMove === 'rock') {
        result = 'You win.';
      } else if (computerMove === 'paper') {
        result = 'Tie.';
      } else if (computerMove === 'scissors') {
        result = 'You lose.';
      }
      
    } else if (playerMove === 'rock') {
      if (computerMove === 'rock') {
        result = 'Tie.';
      } else if (computerMove === 'paper') {
        result = 'You lose.';
      } else if (computerMove === 'scissors') {
        result = 'You win.';
      }
    }
// this if statement is used to update the score Object as per result.
    if (result === 'You win.') {
      score.wins += 1;
    } else if (result === 'You lose.') {
      score.losses += 1;
    } else if (result === 'Tie.') {
      score.ties += 1;
    }

  //using localstorage.setItem() to store the score object in local storage so that the score doesnt get refreshed or restart if the page got closed/reopend/refresh.
    localStorage.setItem('score', JSON.stringify(score));

    updateScoreElement();//calls the function to update score on page.

    document.querySelector('.js-result').innerHTML = result;//show the result on webpage.


    //using template string to add variables with the strings to update the moves paragraph .
    document.querySelector('.js-moves').innerHTML = `You
  <img class="move-icon" src="IMAGES/${playerMove}-emoji.png">
  <img class="move-icon" src="IMAGES/${computerMove}-emoji.png">
  Computer`;
  }

  function updateScoreElement() {// this targets the paragraph of score in html and updates it html by inserting the score object to the page.
    document.querySelector('.js-score')
      .innerHTML = `Wins: ${score.wins}, Losses: ${score.losses}, Ties: ${score.ties}`;
  }

  function pickComputerMove() {
    // here i take a random number with help of math.random() which gives random number from (0-1)
    const randomNumber = Math.random();

    let computerMove = '';
    // here i divide the randomnumber 0-1 number generation into 3 segments with if statements as there are three moves in the games rock,paper,scissors.
    if (randomNumber >= 0 && randomNumber < 1 / 3) {
      computerMove = 'rock';
    } else if (randomNumber >= 1 / 3 && randomNumber < 2 / 3) {
      computerMove = 'paper';
    } else if (randomNumber >= 2 / 3 && randomNumber < 1) {
      computerMove = 'scissors';
    }

    return computerMove;
  }